-- Create a test function to demonstrate RLS in action
CREATE OR REPLACE FUNCTION public.demonstrate_rls()
RETURNS TABLE(test_scenario text, user_context text, result text) AS $$
DECLARE
    test_user uuid;
    result_count int;
BEGIN
    -- Test 1: Alice (owner of General channel) viewing all channels
    test_user := '11111111-1111-1111-1111-111111111111'::uuid;
    EXECUTE format('SET LOCAL app.current_user_id = %L', test_user::text);

    SELECT COUNT(*) INTO result_count FROM public.channels;
    RETURN QUERY SELECT 'Alice viewing channels', 'Alice (owner)', result_count::text;

    SELECT COUNT(*) INTO result_count FROM public.posts;
    RETURN QUERY SELECT 'Alice viewing posts', 'Alice (owner)', result_count::text;

    SELECT COUNT(*) INTO result_count FROM public.comments;
    RETURN QUERY SELECT 'Alice viewing comments', 'Alice (owner)', result_count::text;

    -- Test 2: Bob (moderator of General channel)
    test_user := '22222222-2222-2222-2222-222222222222'::uuid;
    EXECUTE format('SET LOCAL app.current_user_id = %L', test_user::text);

    SELECT COUNT(*) INTO result_count FROM public.channels;
    RETURN QUERY SELECT 'Bob viewing channels', 'Bob (moderator)', result_count::text;

    -- Test 3: Charlie (private user)
    test_user := '33333333-3333-3333-3333-333333333333'::uuid;
    EXECUTE format('SET LOCAL app.current_user_id = %L', test_user::text);

    SELECT COUNT(*) INTO result_count FROM public.channels;
    RETURN QUERY SELECT 'Charlie viewing channels', 'Charlie (private)', result_count::text;

    -- Test 4: Anonymous user (no user_id set)
    RESET app.current_user_id;

    SELECT COUNT(*) INTO result_count FROM public.channels WHERE is_public = true;
    RETURN QUERY SELECT 'Anonymous viewing public channels', 'Anonymous', result_count::text;

    SELECT COUNT(*) INTO result_count FROM public.posts;
    RETURN QUERY SELECT 'Anonymous viewing posts', 'Anonymous', result_count::text;

    -- Reset context
    RESET app.current_user_id;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;"}
